import React, {useState, useEffect} from 'react'
import { Link } from 'react-router-dom'
import { Button } from './Button';

function Navbar() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 960) {
            setButton(false);
        } else {
            setButton(true);        
        }
    };

    useEffect(() => {
        showButton()
    }, []
    );


    window.addEventListener('resize', showButton);



    return (
        <>
        <nav className="navbar">
            <div className="navbar-container">
                <Link to="/" className="navbar-logo">TEST NAME <i className='fas fa-user-astronaut'/></Link>
                <div className='menu-icon' onClick={handleClick}>
                    <i className={click ? 'fas fa-times' : 'fas fa-bars'} ></i>
                </div>
            </div>
            <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                <li className='nav-item'>
                    <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                        Home
                    </Link>
                </li>
                <li className='nav-item'>
                    <Link to='/blog' className='nav-links' onClick={closeMobileMenu}>
                        Blog
                    </Link>
                </li>
                <li className='nav-item'>
                    <Link to='/music' className='nav-links' onClick={closeMobileMenu}>
                        Music
                    </Link>
                </li>
                <li className='nav-item'>
                    <Link to='/other' className='nav-links' onClick={closeMobileMenu}>
                        Other
                    </Link>
                </li>
            </ul>
            {button && <Button buttonStyle='btn--outline'>Follow</Button>}
            
        </nav>
        </>
    )
}

export default Navbar
